import React from 'react';
import { useSelector, useDispatch } from 'react-redux';

import AppSlice, { appSlice } from '../../redux/appSlice';
import mapSlice from '../../redux/mapSlice';
import wheelSlice from '../../redux/wheelSlice';
import { searchSlice } from '../../redux/searchSlice';

import InputBase from '@mui/material/InputBase';
import SearchIcon from '@mui/icons-material/Search';
import { styled, alpha } from '@mui/material/styles';
import AppBar from '@mui/material/AppBar';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Checkbox from '@mui/material/Checkbox';
import FavoriteBorder from '@mui/icons-material/FavoriteBorder';
import Favorite from '@mui/icons-material/Favorite';
import MyLocationIcon from '@mui/icons-material/MyLocation';
import SearchBox from '../parts/searchBox';

import Grid from '@mui/material/Grid';
import ResultDrawer from '../resultdrawer'
import Wheel from '../parts/wheel'
import DetailSearch from '../detailsearch';
import { GoogleIcon } from '../parts/googleIcon'

import { HotPepperIcon } from '../parts/hotPepperIcon'

import { getServiceObject, getUserLocation, getCenter } from '../parts/gMap';
import { createGoogleRequest, combineResults, doGoogleMapSearchPromise, perseGoogleResults, perseHotPepperResults, createHotPepperRequest } from '../logics/map';

import axios from 'axios'
import jsonp from 'axios-jsonp'
import { useTheme } from '@mui/material/styles';
import { auto } from '@popperjs/core';



let userLocation = {
  lat: 35.41221,
  lng: 139.4130
};

let searchResults;




const PartyButton = styled(Button)(({ theme }) => ({

  fontSize: '1.8rem',

  padding: theme.spacing(0, 0, 0, 0),
  textAlign: 'right',

}))

const SearchResultsElementMemo = React.memo(props => {
  return <ResultDrawer status={props.status} results={props.results}></ResultDrawer>;
});

const WheelElementMemo = React.memo(props => {
  return <Wheel trySearchFunc={props.trySearchFunc}></Wheel>;
});

export default function AppBarLayout() {

  const inputValue = useSelector(state => state.app.inputValue);
  const searchResults = useSelector(state => state.mapSlice.searchResults);
  const searchStatus = useSelector(state => state.mapSlice.searchStatus);

  const useGoogle = useSelector(state => state.searchSlice.useGoogle);
  const useHotPepper = useSelector(state => state.searchSlice.useHotPepper);
  const dispatch = useDispatch();
  let keyword;

  const trySearch = (keyword) => {

    //dispatch(AppSlice.actions.handleInputField(keyword));

    let responseHotPepper, responseGoogle, promise1, promise2;
    console.log(getCenter());
    let req = {
      keyword: keyword,
      radius: 500,
      userLoc: getCenter(),
      useGoogle: true,
      useHotPepper: true,
      useGurunabi: true,
      isDone: false,
    };

    if (useHotPepper) {

      promise1 = axios.get(createHotPepperRequest(req), {
        adapter: jsonp
      }).then((response) => {

        responseHotPepper = perseHotPepperResults(response);

      });
    }
    if (useGoogle) {

      promise2 = doGoogleMapSearchPromise(createGoogleRequest(req), getServiceObject()).then((response) => {


        responseGoogle = perseGoogleResults(response);



      }).catch((status) => {
        responseGoogle = perseGoogleResults(status);

      })
    }
    if (promise1 == undefined && promise2 == undefined)
      return;


    Promise.all([promise1, promise2]).then((message) => {
      console.log(promise1);
      console.log(responseHotPepper);

      if (responseGoogle == 'NO_RESULTS' || responseGoogle == undefined || promise1 == undefined) {
        if (responseHotPepper == 'NO_RESULTS' || responseHotPepper == undefined || promise2 == undefined) {

          dispatch(mapSlice.actions.setSearchResults([]));
          dispatch(mapSlice.actions.setSearchStatus('NO_RESULTS'));
        } else {


          dispatch(mapSlice.actions.setSearchResults(responseHotPepper));

          dispatch(mapSlice.actions.setSearchStatus('DOï¼“ï¼’NE'));
        }

      } else {
        if (responseHotPepper == 'NO_RESULTS' || responseHotPepper == undefined || promise2 == undefined) {


          dispatch(mapSlice.actions.setSearchResults(responseGoogle));

          dispatch(mapSlice.actions.setSearchStatus('DOï¼‘ï¼‘ï¼‘NE'));
        } else {


          let combinedResults = combineResults(responseGoogle, responseHotPepper);

          dispatch(mapSlice.actions.setSearchResults(combinedResults));

          dispatch(mapSlice.actions.setSearchStatus('DsONE'));
        }


      }




    });




  }
  return (
    <div>
      <Box
        sx={{ flexGrow: 1 }}>
        <AppBar
          position="static"
          sx={{ zIndex: (theme) => theme.zIndex.drawer + 100 }}
        >
          <Toolbar>
            <Grid 
              container 
              spacing={2}
              display="flex"
              justify="center">
            <Grid item xs={1} md={1}>
              <IconButton
                size="large"
                //edge="start"
                color="inherit"
                aria-label="menu"
                onClick={() => { dispatch(mapSlice.actions.setResultsListToggle()); }}
              >
                <MenuIcon />
              </IconButton>
            </Grid>

            <Grid item xs={6} md={6}>
              <SearchBox
                example="ä¾‹ï¼š"
                requestUserLocMethod={(bool) => { dispatch(mapSlice.actions.requestUserLoc(bool)); }}
                onChangeMethod={(keyword) => { dispatch(AppSlice.actions.handleInputField(keyword)); }}
                searchMethod={(keyword) => { trySearch(keyword) }}></SearchBox>
            </Grid>

            <Grid item xs={2} md={2}>
              <Checkbox
                sx={{ ml: 2 }}
                value={useGoogle} onChange={() => (dispatch(searchSlice.actions.useGoogleToggle()))}
                icon={<GoogleIcon checked={false} />}
                checkedIcon={<GoogleIcon checked={true} />}
              />
              <Checkbox
                sx={{ ml: 2 }}
                value={useHotPepper} onChange={() => (dispatch(searchSlice.actions.useHotPepperToggle()))}
                icon={<HotPepperIcon checked={false} />}
                checkedIcon={<HotPepperIcon checked={true} />}
              />
            </Grid>

            <Grid item xs={1} md={1}>
              <PartyButton
                onClick={() => { dispatch(appSlice.actions.toggleWheel()) }}
                sx={{ ml: 12, textAlign: 'right' }}
                color="warning"
                variant="contained">ðŸŽ¯</PartyButton>
            </Grid>
            </Grid>

          </Toolbar>
        </AppBar>
      </Box>

      <SearchResultsElementMemo status={searchStatus} results={searchResults}></SearchResultsElementMemo>
      <WheelElementMemo trySearchFunc={trySearch}></WheelElementMemo>
    </div>
  );
}
