export function initMap(element, userLoc, radius) {
    console.log("Map Initialized");
    //create an actual gmap 
    let map = new window.google.maps.Map(element, {
        center: userLoc,
        zoom: 15
    });
    //create google places service for nearby serach feature
    let service = new window.google.maps.places.PlacesService(map);

    //draw user and user radius
    let circle = new window.google.maps.Circle({
        center: userLoc,
        fillColor: '#ff0000',
        fillOpacity: 0.2,
        map: map,
        radius: radius * 2,
        strokeColor: '#ff0000',
        strokeOpacity: 1,
        strokeWeight: 1
    });
    let userpos = new window.google.maps.Circle({
        center: userLoc,
        fillColor: '#0000ff',
        fillOpacity: 0.45,
        map: map,
        radius: 20,
        strokeColor: '#0000ff',
        strokeOpacity: 1,
        strokeWeight: 1
    });

    return map

}
