import { createSlice } from '@reduxjs/toolkit';

// Initial values of the state
const appInitialState = {
    mainState: 0,
    appState: 0,
    wheelRotation: 1800,
    wheelContents: ["ラーメン", "カレー", "ハンバーガー", "牛丼", "そば", "ドーナツ", "うどん", "中華"],
    mapResults: [],
    scrollAmount: 0,
    resultsListToggle: false,
};

// creating the slice
export const appSlice = createSlice({
    name: 'app', //このスライスを識別するための名前
    initialState: appInitialState,//ステートの初期値
    reducers: {//ステートを変更するための処理
        setState: (state, action) => {
            state.mainState = action.payload;//実行時に受け取った引数
        },
        setAppState: (state, action) => {
            state.appState = action.payload;//実行時に受け取った引数
        },
        setRotation: (state, action) => {
            state.wheelRotation = action.payload;
        },
        setContents: (state, action) => {
            state.wheelContents = action.payload;
        },
        setResults: (state, action) => {
            state.mapResults = action.payload;
        },
        setScrollAmount: (state, action) => {
            state.scrollAmount = action.payload;
        },
        setResultsListToggle: (state, action) => {
            state.resultsListToggle = !state.resultsListToggle;
        }
    },
});

export default appSlice;
