import './Main.css';
import React, { useState } from 'react';
import { setState } from './redux/appSlice';
import { useSelector, useDispatch } from 'react-redux';

function App() {

    const dispatch = useDispatch();
    const user = useSelector(state => state.mainState);

    return (
        <div className="App">
            <span
        onClick={() => {
          // dispatch関数を実行することで該当のreducerが作動する
          dispatch(setState(1));
        }}
      >{user.reducer.mainState}</span>
        </div>
    );
}

export default App;
