import './base.css';
import React, { useState } from 'react';
import { setState } from './redux/appSlice';
import { useSelector, useDispatch } from 'react-redux';
import { reducer } from './redux/reducer'

function App() {

    const dispatch = useDispatch();
    const app = useSelector(state => state.app);
    let prm = getUserLocation.then(function (value) {
        // 非同期処理が成功した場合
        console.log('実行結果:' + value); // => 実行結果:成功!
    }).catch(function (value) {
            // 非同期処理が失敗した場合
            console.log('実行結果:' + value); // 呼ばれない
    });

    return (
        <div className="App">
            <DrawWheel></DrawWheel>
        </div>
    );
}

const getUserLocation = new Promise((resolve, reject) => {
    navigator.geolocation.getCurrentPosition(resolve, reject)
})


function DrawWheel() {

    const wheelContents = ["Ramen","Sushi","ra","Hamburgers","ww","hazure"];
    const colorCode = ["#FF1493","#FFFFFF","#F0FFFF","#ADFF2F","#FFFFFF","#FFFFFF"];
    let styleObjectArray = [];
    const degreePerContents= 360/wheelContents.length;

    wheelContents.forEach((value,index) => {


        let style = {
            'transform':`rotate(${(index)*degreePerContents}deg)`,
            'borderColor':`${colorCode[index]} transparent`
        }
        styleObjectArray.push(style);
    })

    return (
        <div id="wrapper">
            <div id="wheel">
                <div id="inner-wheel">
                    {wheelContents.map((value, index) => <div style=
                        {styleObjectArray[index+1]}

                        key={index} 
                        id={`contant${index}`}>
                        {value}</div>)}
                </div>
            </div>
        </div>
    )
}

export default App;
