    }

    return (
        <MainDialogBox changeState={changeState}></MainDialogBox>
    );

}

function MainDialogBox(props) {
    const isState = true
    const changeState = (State) => {
        setState(State)
    }
    return (
        <div className="Main-Container">
            <br></br>
            <br></br>
            <GetLocation changeState={changeState}/>
            <p>位置情報は周辺の飲食店情報を取得するためだけに使用されます</p>
            <SkipGetLocation changeState={changeState}/>
        </div>
    );

}

function GetLocation(props) {

    const button = '位置情報の取得を許可する';
    return <span onClick={() => AppState(1)} className="GetLocationButton">{button}</span>;

}

function SkipGetLocation() {
    const button = 'ルーレットだけ遊ぶ';
    return <span onClick={() => AppState(2)} className="SkipGetLocationButton">{button}</span>;

}


export default MainFunction;
