import React, { useEffect } from 'react';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import { useSelector, useDispatch } from 'react-redux';
import { resultListSlice } from '../redux/resultListSlice';
import { width } from '@mui/system';

export default function ResultDrawer(props) {

  let searchResults = props.searchResults;
  let resultsListToggle = useSelector(state => state.app.resultsListToggle);
  console.log(searchResults);

    return (

        <Drawer
            anchor='left'
            open={resultsListToggle}
            variant="persistent"
            PaperProps={{
                sx: {
                  marginTop:8,
                  width:400,
                }
              }}

        >
            <Box>
            {searchResults.map((value, index) => <div
                    className='resultcard'
                    onClick={(index) => {
                        /*
                        map.setZoom(18);
                        let latlng = {
                            lat: value.lat,
                            lng: value.lng

                        }
                        map.setCenter(latlng);
                            */
                    }}
                    key={index}
                    id={`content${index}`}>

                    <div>
                        <div className='resultindex'>{index + 1}</div>
                    </div>
                    <div>
                        <div className='resulttext'>{value.name}</div>
                        <div className='resultvic'>{value.vicinity}</div>
                        <div className='resultrating'>{
                            (() => {
                                let str = '';
                                for (let i = 1; i < value.rating; i++) {
                                    str += '☆';
                                }
                                return <span>{str}</span>
                            })()
                        }</div>
                    </div>
                </div>)}

            </Box>
        </Drawer>

    )


}

