import { createSlice } from '@reduxjs/toolkit';


const InitialState = {
    searchResults : []
};

// creating the slice
export const resultListSlice = createSlice({
    name: 'result', 
    initialState: InitialState,
    reducers: {
        setSearchResult: (state, action) => {
            state.searchResults = action.payload;//実行時に受け取った引数
        }
    },
});

export default resultListSlice;
