import React, { useEffect } from 'react';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import { useSelector, useDispatch } from 'react-redux';
import { mapSlice } from '../redux/mapSlice';
import { width } from '@mui/system';
import ResultCard from './resultcard';
import { createMarker, createCircle } from './mapapi';
import { getMapObject } from './gmap';
import { getUserLocation } from './gmap';

export default function ResultDrawer(props) {

    let searchResults = props.results;
    let resultsListToggle = useSelector(state => state.mapSlice.resultsListToggle);

    for (let i = 0; i <= searchResults.length; i++) {


    }


    return (

        <Drawer
            anchor='left'
            open={resultsListToggle}
            variant="persistent"
            PaperProps={{
                sx: {
                    marginTop: 8,
                    width: 400,
                }
            }}
        >

            <Box>
                {(() => {
                    const d = [];
                    let userPos = getUserLocation();
                    createCircle(userPos.lat,userPos.lng,getMapObject(),1000);
                    if(searchResults.length == 0){

                        d.push(<ResultCard name='検索してみましょう' vicinity='例' mode='noresults'></ResultCard>);
                        return d;

                    }
                    if(searchResults[0].name == 0){
                        d.push(<ResultCard name='何も見つかりませんでした。' vicinity='検索ワード' mode='noresults'></ResultCard>);
                        return d;
                    }
                    for (let i = 0; i < searchResults.length; i++) {

                        let r = searchResults[i];
                        createMarker(r.lat,r.lng, getMapObject());
                        d.push(<ResultCard name={r.name} vicinity={r.vicinity} mode='data'></ResultCard>);
                    }
                    return d;
                })()}

            </Box>
        </Drawer>

    )


}

