import './base.css';
import React, { useState, useEffect } from 'react';
import { setState, setRotation } from './redux/appSlice';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';

import GoogleMapReact from 'google-map-react';

const AnyReactComponent = ({ text }) => <div>{text}</div>;


let countofSpins = 0;

const wheelContents = ["Ramen", "Sushi", "ra", "Hamburgers", "ww", "hazure", "aa", "AA"];
const colorCode = ["#FF1493", "#FFFF1F", "#F0FFFF", "#ADFF2F", "#F33FFF", "#2FFFFF", "#ffffff"];
const wheelRadius = 200; // in px
const apikey = "AIzaSyC9qsxaC4dHG0ibEr9NMKC9Ddro_jdnMk8";
let userLocation = "";
let location = 0;
let isApiLoaded = false;
let drawElement;

function SimpleMap() {
    const defaultProps = {
        center: {
            lat: 10.99835602,
            lng: 77.01502627
        },
        zoom: 11
    };

    return (
        // Important! Always set the container height explicitly
        <div style={{ height: '100vh', width: '100%' }}>
            <GoogleMapReact
                bootstrapURLKeys={{ key: {apikey} }}
                defaultCenter={defaultProps.center}
                defaultZoom={defaultProps.zoom}
            >
                <AnyReactComponent
                    lat={59.955413}
                    lng={30.337844}
                    text="My Marker"
                />
            </GoogleMapReact>
        </div>
    );
}


function App() {

    useEffect(() => {

        if (!isApiLoaded) {


        }

    }, []);

    const dispatch = useDispatch();


    let mainState = useSelector(state => state.reducer.mainState);


    if (userLocation == null) {
        let prm = getUserLocation.then(function (value) {
            // 非同期処理が成功した場合
            console.log('The user location was sucssesfully obtained:');
            userLocation = value;
        }).catch(function (value) {
            // 非同期処理が失敗した場合
            console.log('couldnt obtain the user location:');
            userLocation = null;
        });
    }

    if (mainState != 2) {

        drawElement = <DrawWheel></DrawWheel>;
    } else {

        drawElement = <DrawResult></DrawResult>;
    }



    return (
        <div className="App">
            {drawElement}
        </div>
    );
}

const getUserLocation = new Promise((resolve, reject) => {
    navigator.geolocation.getCurrentPosition(resolve, reject)
})

function getLocationinfo() {

    let longitude = userLocation.coords;
    let latitude = userLocation.coords;
    let radius = 1000;
    console.log(longitude);
    location = 1;

    let url = `https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=${latitude}%2C${longitude}&radius=${radius}&type=restaurant&keyword=cruise&key=${apikey}`;


    const request = axios.request(url)
        .then(function (response) {
            console.log(JSON.stringify(response.data));
        })
        .catch(function (error) {
            console.log("couldnt re");
        });



}
function DrawResult() {


    let wheelRotation = useSelector(state => state.reducer.wheelRotation);

    let resultDegree = wheelRotation % 360;
    let degreePerContent = 360 / wheelContents.length;
    let result = "Error";

    if (degreePerContent > resultDegree) {
        result = wheelContents[0];
    } else {

        for (let i = 1; i < 9; i++) {
            if ((resultDegree - (degreePerContent * i)) <= 0) {
                result = wheelContents[i - 1];
                break;
            }
        }
    }



    console.log(degreePerContent);
    console.log(resultDegree);
    console.log(wheelContents[0]);

    return (
        <div id="wrapper">
            <SimpleMap></SimpleMap>
            <span id="button-1">もう一度やる</span>
            <span id="button-2">ルーレットをカスタマイズ</span>
            <span id="showresult">Result is {result}</span>

        </div >
    );
}



function DrawWheel() {

    let wheelRotation = useSelector(state => state.reducer.wheelRotation);
    let mainState = useSelector(state => state.reducer.mainState);
    const dispatch = useDispatch();

    //ルーレット描画用＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝

    let styleObjectArray = [];
    let degreePerContents = 360 / wheelContents.length;
    let polygon = Math.tan(3.14 / wheelContents.length);
    let sizeofEdge = polygon * wheelRadius;

    wheelContents.forEach((value, index) => {

        let colorCode_temp = colorCode[index]
        if (colorCode_temp == undefined) {
            colorCode_temp = "#FFFFFF";
        }

        let style = {
            'borderWidth': `200px ${sizeofEdge}px 0`,
            'transformOrigin': `${sizeofEdge}px 200px`,
            'left': `${wheelRadius - sizeofEdge}px`,
            'transform': `rotate(${(index) * degreePerContents}deg)`,
            'borderColor': `${colorCode_temp} transparent`
        }

        styleObjectArray.push(style);
    })

    //ここまで＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝

    const WheelSpin = () => {

        if (mainState == 1) {
            //the Whell is runnning!
            return;
        }

        countofSpins++;
        let newSpin = 3000 * countofSpins;
        let rndSpin = Math.floor(Math.random() * (360)) + 1;
        let intervalID = setInterval(() => { dispatch(setState(2)) }, 8000)
        dispatch(setRotation(newSpin + rndSpin));
        dispatch(setState(1));

    }

    return (
        <div id="wrapper">
            <button onClick={WheelSpin}></button>
            <div id="wheel">
                <div id="inner-wheel" style={{
                    transform: `rotate(${wheelRotation}deg)`,
                }}>
                    {wheelContents.map((value, index) => <div style=
                        {styleObjectArray[index]}
                        key={index}
                        id={`content${index}`}>
                        {value}</div>)}
                </div>
                <div id="mid-wheel">
                </div>
            </div>
        </div>
    )
}

export default App;
