import React, { useEffect } from 'react';
import { resultListSlice } from '../redux/resultListSlice';
import { useSelector, useDispatch } from 'react-redux';
import './gmap.css';
import axios from 'axios';

let map, service;
let isMapBusy = true;
const radius = 1000;

function GMap(props) {

    let dispatch = useDispatch();

    useEffect(() => {

        initMap(document.getElementById('map'), props.userLoc, props.radius);

        //a request object for the initial search.
        let request = {
            location: props.userLoc,
            radius: radius,
            type: 'restaurant',//only one type can be specified at once!! should i consider adding the other types like cafe and etcetc?? this can be achived by simply running the another search and add up results

        };
        /*
        getPlace(request).then((response) => {

            let res = resultsParser(response);//places found! put them into parser to parse them for easier prossesing.
            dispatch(resultListSlice.actions.setInitialSearchResult(res));

        }).catch((status) => {

            let res = resultsParser(0); //Nothing found! try again with larger search radius (im gonna work on it!!!!)

        });
        */


    })
    return (
        <div>
            <div id="maphider"></div>
            <div style={{}} id='map'></div>
        </div>
    )
}

function initMap(element, userLoc, radius) {
    //create an actual gmap 
    map = new window.google.maps.Map(element, {
        center: userLoc,
        zoom: 15
    });
    //create google places service for nearby serach feature
    service = new window.google.maps.places.PlacesService(map);

    //draw user and user radius
    let circle = new window.google.maps.Circle({
        center: userLoc,
        fillColor: '#ff0000',
        fillOpacity: 0.2,
        map: map,
        radius: radius * 2,
        strokeColor: '#ff0000',
        strokeOpacity: 1,
        strokeWeight: 1
    });
    let userpos = new window.google.maps.Circle({
        center: userLoc,
        fillColor: '#0000ff',
        fillOpacity: 0.45,
        map: map,
        radius: 20,
        strokeColor: '#0000ff',
        strokeOpacity: 1,
        strokeWeight: 1
    });

}

function CreateMarker(place, index) {

    let marker = new window.google.maps.Marker({
        map: map,
        label: String(index),
        position: place.geometry.location
    });

}


function resultsParser(results) {

    let parsedResults = [];
    if (results == 0 || results.length <= 0) {

        let result_temp = {};
        result_temp.name = '何も見つかりませんでした。';
        result_temp.vicinity = '';
        result_temp.rating = '';
        parsedResults.push(result_temp);
        return parsedResults;

    }
    for (let i = 0; i < results.length; i++) {

        let result_temp = {};
        let result = results[i];
        CreateMarker(result, i + 1);

        result_temp.name = result.name;
        result_temp.vicinity = result.vicinity;
        result_temp.rating = result.rating;
        result_temp.lat = result.geometry.location.lat();
        result_temp.lng = result.geometry.location.lng();
        parsedResults.push(result_temp);
    }

    return parsedResults;

}

function getPlace(request) {
    return new Promise((resolve, reject) => {
        service.nearbySearch(request, (results, status) => {
            if (status === 'OK') {
                resolve(results);
            } else {
                reject(status);
            }
        });
    });
};


export default GMap;
