import {createSlice} from '@reduxjs/toolkit';

// Initial values of the state
const appInitialState = {
  mainState: 0,
  wheelRotation : 1800
};

// creating the slice
export const appSlice = createSlice({
  name: 'app', //このスライスを識別するための名前
  initialState: appInitialState,//ステートの初期値
  reducers: {//ステートを変更するための処理
    setState: (state,action) => {
      state.mainState = action.payload;//実行時に受け取った引数
    },
    setRotation: (state,action) => {
        state.wheelRotation = action.payload;
    }
  },
});

// Action Creatorsをエクスポート
export const {setState,setRotation} = appSlice.actions;

export default appSlice.reducer;
