import React, { useEffect, useRef, ReactElement } from 'react';
import axios from 'axios';
import { useSelector, useDispatch } from 'react-redux';
import './googlemapapi.css';
import { setResults, setScrollAmount } from './redux/appSlice';

const apikey = "AIzaSyC9qsxaC4dHG0ibEr9NMKC9Ddro_jdnMk8"; // the apikey of GoogleMap™️
let map, service, request;
let nearbyResults;

function initMap(element, userLoc, radius) {
    //mapを実際に読み込む
    map = new window.google.maps.Map(element, {
        center: userLoc,
        zoom: 15
    });
    service = new window.google.maps.places.PlacesService(map);

    let circle = new window.google.maps.Circle({
        center: userLoc,       
        fillColor: '#ff0000',   
        fillOpacity: 0.2,    
        map: map,             
        radius: radius,          
        strokeColor: '#ff0000', 
        strokeOpacity: 1,       
        strokeWeight: 1        
    });

}

function CreateMarker(place, radius, index) {



    let placeLoc = place.geometry.location;
    let marker = new window.google.maps.Marker({
        map: map,
        position: place.geometry.location,
        radius:radius
    });
    marker.addListener("click", () => {
        AutoScroll(index);
    });
}

function AutoScroll(index){

    const dispatch = useDispatch();
    dispatch(setScrollAmount(index));

    return(
        <div></div>
    )
}


const getPlace = (request) => {
    return new Promise((resolve, reject) => {
        const service = new window.google.maps.places.PlacesService(map);
        service.nearbySearch(request,(results,status) => {
            if (status === 'OK') {

                resolve(results);
            } else {
                reject(status);
            }    
        });    
    });
};

function resultsController(results,radius){
    let newResults = [];
    for (let i = 0; i < results.length; i++) {
        let newResult = {};
        if(results.length <= 0){
            newResults.name = "I see nothing out there ;(";
            newResults.push(newResult);
            break;
        }
        let result = results[i];
        CreateMarker(result,radius, i);
        if(result.business_status != 'OPERATIONAL'){
            continue;
        }
        /*if(result.opening_hours.open_now != true){
            continue;
        }*/

        newResult.name = result.name;
        newResult.vicinity = result.vicinity;
        newResult.rating = result.rating;
        newResults.push(newResult);
    }
    return newResults;

}


function Gmap(props) {

    let dispatch = useDispatch();
    useEffect(() => {

        initMap(document.getElementById('map'), props.userLoc, props.radius);

        request = {
            location: props.userLoc,
            radius: props.radius,
            keyword: [props.result],
            type: 'restaurant',

        };
        console.log("gmap");
        getPlace(request).then((response) => {
           dispatch(setResults(resultsController(response)));
        });
    })
    return (
        <div style={{}} id="map"></div>
    )
}

export default Gmap;
