import { useSelector, useDispatch } from 'react-redux';
import './gmap.css';
import axios from 'axios';
import {initMap} from './gmapapi'



let shouldMapInitialize = true;
const radius = 1000;

async function asyncCall(req,loc) {
    const result = await searchPlaces(req,loc);
    return result;

}


function GMap(props) {

    let dispatch = useDispatch();

    const searchRequest = useSelector(state => state.app.searchRequest);

    const GmapMemo = React.memo(props => {
        return <h1>Hello {props.name}</h1>;
    });

    if (searchRequest.isDone == false) {

        dispatch(appSlice.actions.setRequestDone());
        let results = searchPlaces(searchRequest, props.userLoc);
