        dispatch(resultListSlice.actions.setSearchResult(asyncCall));

    }

    useEffect(() => {
        if (shouldMapInitialize) {
            initMap(document.getElementById('map'), props.userLoc, props.radius);
            shouldMapInitialize = false;
        }
    })
    return (
        <div>
            <div style={{}} id='map'></div>
        </div>
    )
}

function searchPlaces(req, userLoc) {

    let apiKeyHotPepper = '1ff50644037f0f38';
    const apiURL = 'http://webservice.recruit.co.jp/hotpepper/gourmet/v1/?key=[APIキー]&lat=34.67&lng=135.52&count=100&range=5&order=4';

    //a request object for the initial search.
    let request = {
        location: userLoc,
        radius: req.radius,
        keyword: req.keyword,
        type: 'restaurant',//only one type can be specified at once!! should i consider adding the other types like cafe and etcetc?? this can be achived by simply running the another search and add up results

    };

    getPlace(request).then((response) => {

        let res = resultsParser(response);//places found! put them into parser to parse them for easier prossesing.
        return res;

    }).catch((status) => {

        let res = resultsParser(0); //error in searching.
        return res;

    });

}



function CreateMarker(place, index) {

    let marker = new window.google.maps.Marker({
        map: map,
        label: String(index),
        position: place.geometry.location
    });

}


function resultsParser(results) {

    let parsedResults = [];
    if (results == 0 || results.length <= 0) {

        let result_temp = {};
        result_temp.name = '何も見つかりませんでした。';
        result_temp.vicinity = '';
        result_temp.rating = '';
        parsedResults.push(result_temp);
        return parsedResults;

    }
    for (let i = 0; i < results.length; i++) {

        let result_temp = {};
        let result = results[i];
        CreateMarker(result, i + 1);

        result_temp.name = result.name;
        result_temp.vicinity = result.vicinity;
        result_temp.rating = result.rating;
        result_temp.lat = result.geometry.location.lat();
        result_temp.lng = result.geometry.location.lng();
        parsedResults.push(result_temp);
    }

    return parsedResults;

}

function getPlace(request) {
    return new Promise((resolve, reject) => {
        service.nearbySearch(request, (results, status) => {
            if (status === 'OK') {
                resolve(results);
            } else {
                reject(status);
            }
        });
    });
};


export default GMap;
