export function initMap(element, userLoc) {
    console.log("Map Initialized");
    //create an actual gmap 
    let map = new window.google.maps.Map(element, {
        center: userLoc,
        zoom: 15
    });
    //draw user and user radius
    let userpos = new window.google.maps.Circle({
        center: userLoc,
        fillColor: '#0000ff',
        fillOpacity: 0.45,
        map: map,
        radius: 20,
        strokeColor: '#0000ff',
        strokeOpacity: 1,
        strokeWeight: 1
    });

    return map;

}

export function initService(map) {

    let service = new window.google.maps.places.PlacesService(map);
    return service;
}

export function doGoogleMapSearchPromise(request, service) {

    return new Promise((resolve, reject) => {
        service.nearbySearch(request, (results, status) => {
            if (status === 'OK') {
                resolve(results);
            } else {
                reject(status);
            }
        });
    });

}

export function createGoogleRequest(req) {

    let apiKeyHotPepper = '1ff50644037f0f38';
    const apiURL = 'http://webservice.recruit.co.jp/hotpepper/gourmet/v1/?key=[APIキー]&lat=34.67&lng=135.52&count=100&range=5&order=4';

    //a request object for the initial search.
    let request = {
        location: req.userLoc,
        radius: req.radius,
        keyword: req.keyword,
        type: 'restaurant',//only one type can be specified at once!! should i consider adding the other types like cafe and etcetc?? this can be achived by simply running the another search and add up results

    };

    return request;
}

export function createMarker(latlng, map) {

    let marker = new window.google.maps.Marker({
        map: map,
        position: latlng
    });

}

export function perseGoogleResults(results) {

    let parsedResults = [];
    if (results == 0 || results.length <= 0) {

        let result_temp = {};
        result_temp.name = '何も見つかりませんでした。';
        result_temp.vicinity = '';
        result_temp.rating = '';
        parsedResults.push(result_temp);
        return parsedResults;

    }
    for (let i = 0; i < results.length; i++) {

        let result_temp = {};
        let result = results[i];

        result_temp.name = result.name;
        result_temp.vicinity = result.vicinity;
        result_temp.rating = result.rating;
        result_temp.latlng = result.geometry.location;

        parsedResults.push(result_temp);
    }

    return parsedResults;


}
