import React from 'react';
import { useSelector, useDispatch } from 'react-redux';

import AppSlice, { appSlice } from '../redux/appSlice';
import mapSlice from '../redux/mapSlice';
import wheelSlice from '../redux/wheelSlice';
import { searchSlice } from '../redux/searchSlice';

import InputBase from '@mui/material/InputBase';
import SearchIcon from '@mui/icons-material/Search';
import { styled, alpha } from '@mui/material/styles';
import AppBar from '@mui/material/AppBar';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Checkbox from '@mui/material/Checkbox';
import FavoriteBorder from '@mui/icons-material/FavoriteBorder';
import Favorite from '@mui/icons-material/Favorite';
import MyLocationIcon from '@mui/icons-material/MyLocation';

import ResultDrawer from '../components/resultdrawer'
import Wheel from '../components/wheel'
import DetailSearch from '../components/detailsearch';
import { GoogleIcon } from '../components/icons/googleicon'

import { HotPepperIcon } from '../components/icons/hotpeppericon'

import { getServiceObject, getUserLocation } from '../components/gmap';
import { createGoogleRequest, combineResults, doGoogleMapSearchPromise, perseGoogleResults, perseHotPepperResults, createHotPepperRequest } from '../components/mapapi';

import axios from 'axios'
import jsonp from 'axios-jsonp'
import { useTheme } from '@mui/material/styles';
import { auto } from '@popperjs/core';



let userLocation = {
  lat: 35.41221,
  lng: 139.4130
};

let searchResults;


const Search = styled('div')(({ theme }) => ({
  position: 'relative',
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  '&:hover': {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginLeft: 0,
  width: '40%',
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: '100%',
  position: 'absolute',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  zIndex: 2000
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: 'inherit',
  width: '80%',
  '& .MuiInputBase-input': {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create('width'),
  },
}));

const PartyButton = styled(Button)(({ theme }) => ({

  fontSize: '1.8rem',

  padding: theme.spacing(0, 0, 0, 0),
  textAlign: 'right',

}))

const SearchResultsElementMemo = React.memo(props => {
  return <ResultDrawer status={props.status} results={props.results}></ResultDrawer>;
});

const WheelElementMemo = React.memo(props => {
  return <Wheel></Wheel>;
});

export default function AppBarLayout() {

  const inputValue = useSelector(state => state.app.inputValue);
  const searchResults = useSelector(state => state.mapSlice.searchResults);

  const searchStatus = useSelector(state => state.mapSlice.searchStatus);
  console.log(searchStatus);
  let useGoogle = true;
  let useHotPepper = true;
  const dispatch = useDispatch();
  let keyword;

  const trySearch = () => {

    let responseHotPepper, responseGoogle;

    let req = {
      keyword: inputValue,
      radius: 1000,
      userLoc: getUserLocation(),
      useGoogle: true,
      useHotPepper: true,
      useGurunabi: true,
      isDone: false,
    };

    let promise1 = axios.get(createHotPepperRequest(req), {
      adapter: jsonp
    }).then((response) => {
      console.log(response);
      responseHotPepper = perseHotPepperResults(response);

    });

    let promise2 = doGoogleMapSearchPromise(createGoogleRequest(req), getServiceObject()).then((response) => {


      responseGoogle = perseGoogleResults(response);



    }).catch((status) => {
      responseGoogle = perseGoogleResults(status);

    })


    Promise.all([promise1, promise2]).then((message) => {
      if (responseGoogle == 'NO_RESULTS') {
        if (responseHotPepper == 'NO_RESULTS') {

          dispatch(mapSlice.actions.setSearchResults([]));
          dispatch(mapSlice.actions.setSearchStatus('NO_RESULTS'));
        } else {


          dispatch(mapSlice.actions.setSearchResults(responseHotPepper));

          dispatch(mapSlice.actions.setSearchStatus('DONE'));
        }

      } else {
        if (responseHotPepper == 'NO_RESULTS') {

          dispatch(mapSlice.actions.setSearchResults(responseGoogle));

          dispatch(mapSlice.actions.setSearchStatus('DONE'));
        } else {


          let combinedResults = combineResults(responseGoogle, responseHotPepper);

          dispatch(mapSlice.actions.setSearchResults(combinedResults));

          dispatch(mapSlice.actions.setSearchStatus('DONE'));
        }


      }




    });




  }

  const handleChange = (e) => {
    dispatch(AppSlice.actions.handleInputField(e.target.value));
  }

  return (
    <div>
      <Box
        sx={{ flexGrow: 1 }}>
        <AppBar
          position="static"
          sx={{ zIndex: (theme) => theme.zIndex.drawer + 100 }}
        >
          <Toolbar>

            <IconButton
              size="large"
              edge="start"
              color="inherit"
              aria-label="menu"
              sx={{ mr: 2 }}
              onClick={() => {
                dispatch(mapSlice.actions.setResultsListToggle());
              }}
            >
              <MenuIcon />
            </IconButton>

            <Search>
              <SearchIconWrapper>
                <IconButton
                  size="large"
                  edge="start"
                  color="inherit"
                  aria-label="menu"
                  sx={{ mr: 2 }}
                  onClick={() => {
                    trySearch();
                  }}
                >
                  <SearchIcon />
                </IconButton>
              </SearchIconWrapper>
              <StyledInputBase

                onChange={handleChange}
                ref={keyword}
                placeholder="例：ラーメン　二郎系"

              />

              <IconButton
                size="large"
                edge="start"
                color="warning"

                aria-label="menu"
                onClick={() => { dispatch(mapSlice.actions.requestUserLoc(true)) }}
              >
                <MyLocationIcon />
              </IconButton>
            </Search>
            <Button sx={{ ml: 1.5 }} onClick={() => { dispatch(searchSlice.actions.detailBoxToggle()) }} variant="contained">Advanced</Button>
            <Checkbox

              sx={{ ml: 2 }}
              value={useGoogle} onChange={() => (dispatch(searchSlice.actions.useGoogleToggle(!useGoogle)))}
              icon={<GoogleIcon checked={false} />}
              checkedIcon={<GoogleIcon checked={true} />}
            />
            <Checkbox

              sx={{ ml: 2 }}
              value={useHotPepper} onChange={() => (dispatch(searchSlice.actions.useHotPepperToggle(!useHotPepper)))}
              icon={<HotPepperIcon />}
              checkedIcon={<HotPepperIcon />}
            />
            <PartyButton onClick={() => { dispatch(appSlice.actions.toggleWheel()) }} sx={{ ml: 2, textAlign: 'right' }} color="warning" variant="contained">🎯</PartyButton>
          </Toolbar>
        </AppBar>
      </Box>
      <DetailSearch
        sx={{ ml: 12 }} >

      </DetailSearch>
      <SearchResultsElementMemo status={searchStatus} results={searchResults}></SearchResultsElementMemo>
      <WheelElementMemo></WheelElementMemo>
    </div>
  );
}
