import React, { useEffect } from 'react';
import { resultListSlice } from '../redux/resultListSlice';
import { appSlice } from '../redux/appSlice';
import { mapSlice} from '../redux/mapSlice';
import { useSelector, useDispatch } from 'react-redux';
import './gmap.css';
import axios from 'axios';
import { initService, doGoogleMapSearchPromise, createRequest, createMarker } from './mapapi'


let map, service;
let shouldMapInitialize = true;
let hasUserLocation, isUserLocRequested = false;
let userLocation = {
    lat: 35.41221,
    lng: 139.4130
};
const radius = 1000;



function GMap(props) {

    let dispatch = useDispatch();

    const searchRequest = useSelector(state => state.app.searchRequest);
    const selectorMapSlice = useSelector(state => state.mapSlice.isUserLocRequested);

    const GmapMemo = React.memo(props => {
        return <h1>Hello {props.name}</h1>;
    });

    if (searchRequest.isDone == false) {

        dispatch(appSlice.actions.setRequestDone());
        let req = createRequest(searchRequest, props.userLoc);

        doGoogleMapSearchPromise(req, service).then((response) => {

            let res = resultsParser(response);//places found! put them into parser to parse them for easier prossesing.
            dispatch(resultListSlice.actions.setSearchResult(res));

        }).catch((status) => {

            let res = resultsParser(0); //error in searching.
            dispatch(resultListSlice.actions.setSearchResult(res));

        });

    }

    useEffect(() => {


        if (shouldMapInitialize) {
            initMap(document.getElementById('map'), props.userLoc, props.radius);
            service = initService(map);
            shouldMapInitialize = false;
        }
    })



    return (
        <div>
            <div style={{}} id='map'></div>
        </div>
    )
}

function initMap(element, userLoc, radius) {
    console.log("Map Initialized");
    //create an actual gmap 
    map = new window.google.maps.Map(element, {
        center: userLoc,
        zoom: 15
    });
    //draw user and user radius
    let circle = new window.google.maps.Circle({
        center: userLoc,
        fillColor: '#ff0000',
        fillOpacity: 0.2,
        map: map,
        radius: radius * 2,
        strokeColor: '#ff0000',
        strokeOpacity: 1,
        strokeWeight: 1
    });
    let userpos = new window.google.maps.Circle({
        center: userLoc,
        fillColor: '#0000ff',
        fillOpacity: 0.45,
        map: map,
        radius: 20,
        strokeColor: '#0000ff',
        strokeOpacity: 1,
        strokeWeight: 1
    });

}
const resultsParser = (results) => {

    let parsedResults = [];
    if (results == 0 || results.length <= 0) {

        let result_temp = {};
        result_temp.name = '何も見つかりませんでした。';
        result_temp.vicinity = '';
        result_temp.rating = '';
        parsedResults.push(result_temp);
        return parsedResults;

    }
    for (let i = 0; i < results.length; i++) {

        let result_temp = {};
        let result = results[i];
        createMarker(result, i + 1, map);

        result_temp.name = result.name;
        result_temp.vicinity = result.vicinity;
        result_temp.rating = result.rating;
        result_temp.lat = result.geometry.location.lat();
        result_temp.lng = result.geometry.location.lng();
        parsedResults.push(result_temp);
    }

    return parsedResults;

}


const getUserLocation = new Promise((resolve, reject) => {
    navigator.geolocation.getCurrentPosition(resolve, reject)
})


export default GMap;
