import './Main.css';
import React, { useState } from 'react';


function MainFunction(){
    const [AppState, setState] = useState(0);

    const ChangeState = (State) => {
        setState(State);
    }

    return (
        <MainDialogBox ChangeState={ChangeState}></MainDialogBox>
    );

}

function MainDialogBox(props) {

    const ChangeState_c = (State) => {
        props.ChangeState(State);
    }
    return (
        <div className="Main-Container">
            <br></br>
            <br></br>
            <GetLocation ChangeState_c={ChangeState_c}/>
            <p>位置情報は周辺の飲食店情報を取得するためだけに使用されます</p>
            <p>{props.AppState}a</p>
            <SkipGetLocation ChangeState_c={ChangeState_c}/>
        </div>
    );

}

function GetLocation(props) {

    const button = '位置情報の取得を許可する';
    return <span onClick={() => props.ChangeState_c(1)} className="GetLocationButton">{button}</span>;

}

function SkipGetLocation(props) {
    const button = 'ルーレットだけ遊ぶ';
    return <span onClick={() => props.ChangeState_c(2)} className="SkipGetLocationButton">{button}</span>;

}


export default MainFunction;
