import React, { useEffect, useRef, ReactElement } from 'react';
import axios from 'axios';
import { useSelector, useDispatch } from 'react-redux';
import './googlemapapi.css';
import { setResults } from './redux/appSlice';

const apikey = "AIzaSyC9qsxaC4dHG0ibEr9NMKC9Ddro_jdnMk8"; // the apikey of GoogleMap™️
let map, service, request;
let nearbyResults;

function initMap(element, userLoc) {

    map = new window.google.maps.Map(element, {
        center: userLoc,
        zoom: 15
    });
    service = new window.google.maps.places.PlacesService(map);
    let circle = new window.google.maps.Circle({
        center: userLoc,       // 中心点(google.maps.LatLng)
        fillColor: '#ff0000',   // 塗りつぶし色
        fillOpacity: 0.2,       // 塗りつぶし透過度（0: 透明 ⇔ 1:不透明）
        map: map,             // 表示させる地図（google.maps.Map）
        radius: 300,          // 半径（ｍ）
        strokeColor: '#ff0000', // 外周色
        strokeOpacity: 1,       // 外周透過度（0: 透明 ⇔ 1:不透明）
        strokeWeight: 1         // 外周太さ（ピクセル）
    });

}

function createMarker(place, ifInitPoint) {
    let placeLoc = place.geometry.location;
    let marker = new window.google.maps.Marker({
        map: map,
        position: place.geometry.location,
    });
}


const getPlace = (request) => {
    return new Promise((resolve, reject) => {
        const service = new window.google.maps.places.PlacesService(map);
        service.nearbySearch(request,(results,status) => {
            if (status === 'OK') {

                resolve(results);
            } else {
                reject(status);
            }    
        });    
    });
};

function resultsController(results){
    let newResults = [];
    for (let i = 0; i < results.length; i++) {
        let result = results[i];
        createMarker(result);
        if(result.business_status != 'OPERATIONAL'){
            continue;
        }
        /*if(result.opening_hours.open_now != true){
            continue;
        }*/
        let newResult = {};
        newResult.name = result.name;
        newResults.push(newResult);
    }
    return newResults;

}


function Gmap(props) {

    let dispatch = useDispatch();
    useEffect(() => {

        initMap(document.getElementById('map'), props.userLoc);

        request = {
            location: props.userLoc,
            radius: '500',
            keyword: ['ラーメン']
        };

        getPlace(request).then((response) => {
           dispatch(setResults(resultsController(response)));
        });
    })
    return (
        <div style={{}} id="map"></div>
    )
}

export default Gmap;
