import React from 'react';
import { useSelector, useDispatch } from 'react-redux';

import AppSlice, { appSlice } from '../../redux/appSlice';
import mapSlice from '../../redux/mapSlice';
import wheelSlice from '../../redux/wheelSlice';
import { searchSlice } from '../../redux/searchSlice';

import InputBase from '@mui/material/InputBase';
import SearchIcon from '@mui/icons-material/Search';
import { styled, alpha } from '@mui/material/styles';
import AppBar from '@mui/material/AppBar';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';

import MyLocationIcon from '@mui/icons-material/MyLocation';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Checkbox from '@mui/material/Checkbox';
import DetailSearch from '../detailsearch';
import { getServiceObject, getUserLocation, getCenter } from '../parts/gMap';
import { createGoogleRequest, combineResults, doGoogleMapSearchPromise, perseGoogleResults, perseHotPepperResults, createHotPepperRequest } from '../logics/map';

const Search = styled('div')(({ theme }) => ({
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: alpha(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    marginLeft: 0,
    width: '100%',
  }));

  const SearchIconWrapper = styled('div')(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 2000
  }));

  const StyledInputBase = styled(InputBase)(({ theme }) => ({
    color: 'inherit',
    width: '80%',
    '& .MuiInputBase-input': {
      padding: theme.spacing(1, 1, 1, 0),
      // vertical padding + font size from searchIcon
      paddingLeft: `calc(1em + ${theme.spacing(4)})`,
      transition: theme.transitions.create('width'),
    },
  }));

export default function SearchBox(props) {
    /*
    [props]
    onChangeMethod
    example
    requestUserLocMethod
    searchMethod
    */

    let keyword;

    return (
        <Search>
            <SearchIconWrapper>
                <IconButton
                    size="large"
                    edge="start"
                    color="inherit"
                    aria-label="menu"
                    onClick={() => {
                        props.searchMethod(keyword);
                    }}
                >
                    <SearchIcon />
                </IconButton>
            </SearchIconWrapper>
            <StyledInputBase

                onChange={props.onChangeMethod(keyword)}
                ref={keyword}
                placeholder={props.example}

            />

            <IconButton
                size="large"
                edge="start"
                color="warning"
                aria-label="menu"
                onClick={props.requestUserLocMethod(true)}
            >
                <MyLocationIcon />
            </IconButton>
        </Search>


    )
}
