import React, { useEffect } from 'react';
import { appSlice } from '../redux/appSlice';
import { mapSlice } from '../redux/mapSlice';
import { useSelector, useDispatch } from 'react-redux';
import './gmap.css';
import axios from 'axios';
import { initMap, initService, doGoogleMapSearchPromise, createGoogleRequest, createMarker } from './mapapi'



let map, service;
let shouldMapInitialize = true;
let userLocation = {
    lat: 35.41221,
    lng: 139.4130
};

const radius = 1000;

export const getMapObject = () => {

    return map;

}

export const getServiceObject = () => {

    return service;

}



function GMap(props) {

    let dispatch = useDispatch();

    const searchRequest = useSelector(state => state.app.searchRequest);
    const isUserLocRequested = useSelector(state => state.mapSlice.isUserLocRequested);

    const GmapMemo = React.memo(props => {
        return <h1>Hello {props.name}</h1>;
    });

    /*if (searchRequest.isDone == false) {

        dispatch(appSlice.actions.setRequestDone());

        let req = createGoogleRequest(searchRequest, props.userLoc);

        doGoogleMapSearchPromise(req, service).then((response) => {

            let res = resultsParser(response);//places found! put them into parser to parse them for easier prossesing.
            dispatch(resultListSlice.actions.setSearchResult(res));

        }).catch((status) => {

            let res = resultsParser(0); //error in searching.
            dispatch(resultListSlice.actions.setSearchResult(res));

        });

    }*/

    useEffect(() => {

        if (isUserLocRequested) {
            let prm = getUserLocation.then(function (value) {

                userLocation = {
                    lat: value.coords.latitude,
                    lng: value.coords.longitude
                };

                map.panTo(userLocation);


            }).catch(value => {

                console.log("unable to obtain the user position")

            }).finally(() => {

                dispatch(mapSlice.actions.requestUserLoc(false));


            });


        }

        if (shouldMapInitialize) {
            map = initMap(document.getElementById('map'), userLocation);
            service = initService(map);
            shouldMapInitialize = false;
        }
    })



    return (
        <div>
            <div style={{}} id='map'></div>
        </div>
    )
}





const getUserLocation = new Promise((resolve, reject) => {
    navigator.geolocation.getCurrentPosition(resolve, reject)
})


export default GMap;
