import { getServiceObject, getUserLocation, getCenter } from './map';
import AppSlice, { appSlice } from '../../redux/appSlice';
import mapSlice from '../../redux/mapSlice';
import wheelSlice from '../../redux/wheelSlice';
import { searchSlice } from '../../redux/searchSlice';
import { combineResults, doGoogleMapSearchPromise, perseGoogleResults, perseHotPepperResults} from '../logics/map';

import axios from 'axios'
import jsonp from 'axios-jsonp'

export const trySearch = (keyword,useGoogle,useHotPepper) => {

    let responseHotPepper, responseGoogle, promiseGoogle, promiseHotPepper;

    let req = {
      keyword: keyword,
      radius: 500,
      userLoc: getCenter(),
      useGoogle: true,
      useHotPepper: true,
      useGurunabi: true,
      isDone: false,
    };

    if (useHotPepper) {

      promiseHotPepper = axios.get(createHotPepperRequest(req), {
        adapter: jsonp
      }).then((response) => {

        responseHotPepper = perseHotPepperResults(response);

      });
    }
    if (useGoogle) {

      promise2 = doGoogleMapSearchPromise(createGoogleRequest(req), getServiceObject()).then((response) => {


        responseGoogle = perseGoogleResults(response);



      }).catch((status) => {
        responseGoogle = perseGoogleResults(status);

      })
    }
    if (promise1 == undefined && promise2 == undefined)
      return;


    Promise.all([promise1, promise2]).then((message) => {
      console.log(promise1);
      console.log(responseHotPepper);

      if (responseGoogle == 'NO_RESULTS' || responseGoogle == undefined || promise1 == undefined) {
        if (responseHotPepper == 'NO_RESULTS' || responseHotPepper == undefined || promise2 == undefined) {

          //dispatch(mapSlice.actions.setSearchResults([]));
          //dispatch(mapSlice.actions.setSearchStatus('NO_RESULTS'));
          return [];
        } else {


          //dispatch(mapSlice.actions.setSearchResults(responseHotPepper));

          //dispatch(mapSlice.actions.setSearchStatus('DO３２NE'));
          return responseHotPepper;
        }

      } else {
        if (responseHotPepper == 'NO_RESULTS' || responseHotPepper == undefined || promise2 == undefined) {


          //dispatch(mapSlice.actions.setSearchResults(responseGoogle));

          //dispatch(mapSlice.actions.setSearchStatus('DO１１１NE'));
          return responseGoogle;
        } else {


          return combineResults(responseGoogle, responseHotPepper);

          //dispatch(mapSlice.actions.setSearchResults(combinedResults));

          //dispatch(mapSlice.actions.setSearchStatus('DsONE'));
        }


      }




    })};


export function createGoogleRequest(req) {


    //a request object for the initial search.
    let request = {
        location: req.userLoc,
        radius: req.radius,
        keyword: req.keyword,
        type: 'restaurant',//only one type can be specified at once!! should i consider adding the other types like cafe and etcetc?? this can be achived by simply running the another search and add up results

    };

    return request;
}

export function createHotPepperRequest(req) {

    let apiKeyHotPepper = '1ff50644037f0f38';
    let lat = req.userLoc.lat();
    let lng = req.userLoc.lng();
    let keyword = req.keyword;
    const apiURL = `http://webservice.recruit.co.jp/hotpepper/gourmet/v1/?key=${apiKeyHotPepper}&lat=${lat}&lng=${lng}&count=100&range=3&keyword=${keyword}&order=4&format=jsonp`;

    return apiURL;
}
