import React, { useEffect } from 'react';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import { useSelector, useDispatch } from 'react-redux';
import { mapSlice } from '../redux/mapSlice';
import { width } from '@mui/system';
import ResultCard from './parts/resultCard';
import { createMarker, createCircle } from './logics/map';
import { getMapObject } from './parts/gMap';
import { getUserLocation } from './parts/gMap';

export default function ResultDrawer(props) {

    let searchResults = props.results;
    let searchStatus = props.status;
    console.log(searchStatus);
    let userCircle;
    let resultsListToggle = useSelector(state => state.mapSlice.resultsListToggle);


    return (

        <Drawer
            anchor='left'
            open={resultsListToggle}
            variant="persistent"
            PaperProps={{
                sx: {
                    marginTop: 8,
                    width: 400,
                }
            }}
        >

            <Box>
                {(() => {
                    const d = [];
                    let userPos = getUserLocation();

                    if(userCircle){

                        userCircle.setMap(null);
                    }


                    if(searchStatus == 'INITIALIZED'){

                        d.push(<ResultCard name='検索してみましょう' vicinity='😀' mode='noresults'></ResultCard>);
                        return d;

                    }

                    if(searchStatus == 'NO_RESULTS'){

                        d.push(<ResultCard name='何も見つかりませんでした。' vicinity='🥺' mode='noresults'></ResultCard>);
                        return d;
                    }

                    for (let i = 0; i < searchResults.length; i++) {

                        let r = searchResults[i];
                        createMarker(r.lat,r.lng, getMapObject(),r.type);
                        d.push(<ResultCard 
                            key={r.name}
                            name={r.name} 
                            vicinity={r.vicinity} 
                            rating={r.rating} 
                            reviews={r.reviews}
                            mode='data'
                            eatfree={r.eatfree}
                            drinkfree={r.drinkfree}
                            parking={r.parking}
                            card={r.card}
                            rat={r.rat}
                            privateroom={r.privateroom}
                            google={r.google}
                            hotpepper={r.hotpepper}
                            ></ResultCard>);

                    }
                    userCircle = createCircle(userPos.lat,userPos.lng,getMapObject(),1000);
                    return d;
                })()}

            </Box>
        </Drawer>

    )


}

