import './Main.css';
import React, { useState } from 'react';

const state = 0;
export const AppState = React.createContext(state);


function MainFunction() {
    const [AppState, setState] = useState(0);


    const ChangeState = (State) => {
        if (AppState == State)
            return;

        if (State == 1) {
            //state = 1 waiting user's response in the title screen.
            //state = 2 Obtaining user location approved. now getting user loc.
            //state = 3 

            State = 2;
            GetUserLocation().then(ok => {
                alert('erroraa');
            }).catch(err => {
                alert('Couldnt obtain the location');
            })
        };

        setState(State);
        console.log(State)

    };

    const GetUserLocation = (options) => {
        return new Promise((resolve, reject) => {
            navigator.geolocation.getCurrentPosition(resolve, reject, options)
        })
    }

    return (
        <MainDialogBox></MainDialogBox>
    );

}

const MainDialogBox = (props) => {

    const [SubState, setState] = useState(0);


    const ChangeState_c = (State) => {

        if(State==1){
            setState(GetGPS());
        }else{
        setState(State);
        }
    }

    return (
        <div className="Main-Container">
            <br></br>
            <br></br>
            <GetLocation ChangeState_c={ChangeState_c} />
            <p>位置情報は周辺の飲食店情報を取得するためだけに使用されます</p>
            <MessageBox text={SubState} />
            <br></br>
            <SkipGetLocation ChangeState_c={ChangeState_c} />
        </div>
    );

}

function GetLocation(props) {

    const button = '位置情報の取得を許可する';
    return <span onClick={() => props.ChangeState_c(1)} className="GetLocationButton">{button}</span>;

}

function GetGPS(props) {

    let state = 1;
    const GetUserLocation = (options) => {
        return new Promise((resolve, reject) => {
            navigator.geolocation.getCurrentPosition(resolve, reject, options);
            resolve(2);
            reject(3);
        })
    }

    GetUserLocation()
    .then((position) => {
        state=position;
    })
    .catch((err) => {
        state=err;
    });

    return state;

}

function MessageBox(props) {



    let state;
    if (state == props.text)
        return;

    state = props.text;
    let msg;
    if (state == 1) {

        msg = ("位置情報を取得中");
    } else if (state == 2) {
        msg = ("位置情報を取得しました");
    } else if (state == 0) {
        msg = ("");
    } else if (state == 3) {
        msg = ("位置情報の取得に失敗しました。");
    }

    return <span>{msg}</span>

}

function SkipGetLocation(props) {
    const button = 'ルーレットだけ遊ぶ';
    return <span onClick={() => props.ChangeState_c(2)} className="SkipGetLocationButton">{button}</span>;

}


export default MainFunction;
